{
case 10: //ReadProcessData
    if ( msg.BNIPort == 255 )
    {
        msg.portData = [];
        var dataLength = (msg.returnObject[0] | ( msg.returnObject[1] << 8 ))-5;
        var offset = 5;
        var itemLength;

        while ( dataLength > 0 )
        {
            itemLength = msg.returnObject[ offset ];

            if ( itemLength + 1 > dataLength )
            {
                // Error - data says it's longer than the buffer.
                break;
            }

            if ( itemLength < 1 )
            {
                // Error - invalid data length
                break;
            }

          msg.portData.push({
                "data":    msg.returnObject.slice( offset + 1, offset + itemLength ),
      "PDvalid": msg.returnObject[ offset + itemLength ]
            });

            offset += itemLength + 1;
     dataLength -= itemLength + 1;
        }
    }
    else
    {
        msg.dataLength = (msg.returnObject[0] | ( msg.returnObject[1] << 8 ))-6;
        msg.PDValid = msg.returnObject[msg.returnObject.length-1];
   }
    break;
case 11: //WriteProcessData
    break;
case 9: //ALReadReq
    break;
case 8: //ALWriteReq
    break;
case 6: //ALReadError
    break;
case 7: //ALWriteError
    break;
default:
    break;
}

return msg;

